<?phprequire_once './configs/app.php';function getServices(){    $response = array('status' => false, 'data' => []);    try    {        $sql = "SELECT id, name, description AS info, image FROM services WHERE state_id = 2 ORDER BY id ASC";        $db = new PDO("mysql:host=".APP_HOST.";dbname=".APP_DB_NAME, APP_DB_USER, APP_DB_PASS);        $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        $stmt = $db->prepare($sql);        $stmt->execute();        $results = $stmt->fetchAll(PDO::FETCH_ASSOC);        $response = array('status' => true, 'data' => $results);    } catch (PDOException $e) {        $response['status'] = "Error: " . $e->getMessage();    }    return json_encode($response);}