<?phprequire_once './../configs/app.php';function connect(){    $host = APP_HOST;    $dbName = APP_DB_NAME;    $user = APP_DB_USER;    $pass = APP_DB_PASS;    try {        $db = new \PDO("mysql:host={$host};dbname={$dbName}", $user, $pass);        $db->setAttribute(\PDO::ATTR_ERRMODE, \PDO::ERRMODE_EXCEPTION);        return $db;    } catch (\PDOException $e) {        return die("Connection failed: " . $e->getMessage());    }}function getServices(){    try {        $sql = "SELECT id, name, description AS desc, image, state_id FROM services WHERE state_id = 1 ORDER BY name ASC";        $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        $stmt = $db->prepare($sql);        $stmt->execute();        $services = $stmt->fetchAll(PDO::FETCH_ASSOC);        return json_encode($services);    } catch (PDOException $e) {        return "Error: " . $e->getMessage();    }}